//var bcrypt = require('bcrypt');

module.exports = (function () {
    var _return = {};

    _return.hash_ = function (word, callback) {
        bcrypt.genSalt(10, function (err, salt) {
            bcrypt.hash(String(word), salt, function (err, hash) {
                if (err) throw err;

                if (callback) callback(hash);
            });
        });
    };

    _return.compare_ = function (word, hash, callback) {
        bcrypt.compare(String(word), hash, function (err, res) {
            if (err) throw err;
            if (callback) callback(res);
        });
    };

    _return.hash = function (word, callback) {
        if(callback) callback(word);
    };

    _return.compare = function (word, hash, callback) {
        if( word == hash && callback ) callback(true);
        else if(callback) callback(false);
    };


    return _return;
})();
