
var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// making schemas for inserting data in mongo

var users_schema = Schema({
    first_name: String ,
    last_name: String ,
    email: String ,
    student_number : Number ,
    codeforces_username : String ,
    mobile : String ,
    _verify : Boolean ,
    contests : [{
        be_score : Number ,
        solved_q_summary : [{
            title : String ,
            n : Number
        }],
        max_question_score : Number ,
        questions : [{
            title : String ,
            all_accepts : Number ,
            wrongs : Number ,
            time : Number
        }]
    }] ,
    meetings : [{
        date : String
    }]
});
var

 logs_schema = Schema({
    log : String
});



module.exports = (function () {
    var _return = {};

    _return.users = mongoose.model('users', users_schema);
    _return.logs = mongoose.model('log', logs_schema);

    return _return;
})();
